package och02;

public class VarEx02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float n1 =33;
		float n2 = 10;
		double result;
		
		result = n1+n2;
		System.out.printf("%.1f %c %.1f= %.1f\n", n1,'+',n2,result);
		result = n1-n2;
		System.out.printf("%.1f %c %.1f= %.1f\n", n1,'-',n2,result);
		result = n1*n2;
		System.out.printf("%.1f %c %.1f= %.1f\n", n1,'*',n2,result);
		result = n1/n2;
		System.out.printf("%.1f / %.1f= %.1f\n", n1,n2,result);
		
		

	}

}
